// Package utilities provides common helper functions and error types used
// across the application.
package utilities

import "fmt"

// CommentInsertionError is returned when adding a "generated by" comment
// to a file fails. It wraps the underlying file I/O error.
type CommentInsertionError struct {
	Err error
}

// Error implements the error interface for CommentInsertionError.
func (c *CommentInsertionError) Error() string {
	return fmt.Sprintf("CommentInsertionError-> %v", c.Err)
}

// ListFilesError is returned when listing files in a directory fails.
// It wraps the underlying file system error.
type ListFilesError struct {
	Err error
}

// Error implements the error interface for ListFilesError.
func (l *ListFilesError) Error() string {
	return fmt.Sprintf("ListFilesError-> %v", l.Err)
}

// IncludePathError is returned when gathering include paths from a directory
// fails. It wraps the underlying file system error.
type IncludePathError struct {
	Err error
}

// Error implements the error interface for IncludePathError.
func (i *IncludePathError) Error() string {
	return fmt.Sprintf("IncludePathError-> %v", i.Err)
}
